{"changed":true,"filter":false,"title":"interfaz.rb","tooltip":"/lib/Examenes/interfaz.rb","value":"# -*- coding: utf-8 -*-\nrequire 'rubygems'\nrequire 'bundler/setup'\nrequire 'rspec'\n\nmodule Exam\n  class UI\n    attr_reader :contenido, :aciertos, :nota\n    \n    \n    def initialize (examen)\n      @contenido = examen\n      @aciertos = 0\n      @nota = -1\n      @numeroPreguntas = examen.lista_preguntas.count\n    end\n    \n    \n    def mostrarPregunta(numero)\n      print \"Pregunta\"\n      puts numero\n      return @contenido.obtenerPregunta(numero)\n    end\n    \n    \n    def input(numeroPregunta, resp=0)\n      puts \"\"\n      print \"Respuesta: \"\n      STDOUT.flush\n      if resp == 0 then\n        respuesta = gets.chomp\n      else\n        respuesta = resp\n        puts respuesta\n      end\n      puts \"\"\n      puts \"\"\n      puts \"\"\n      puts \"\"\n      comparar(numeroPregunta, respuesta)\n      return true\n    end\n    \n    \n    def comparar(numeroPregunta, respuesta)\n      if respuesta == @contenido.lista_soluciones.obtenerValor(numeroPregunta) then\n        @aciertos = @aciertos + 1\n        return true\n      end\n    end\n\n\n    def notaFinal\n      print \"Aciertos: \"\n      print @aciertos\n      print \" / \"\n      puts @numeroPreguntas\n      print \"Su nota ha sido: \"\n      @nota = ((@aciertos * 10) / @numeroPreguntas)\n      print @nota\n      return @nota\n    end\n    \n    \n    def cabecera\n      puts \"\"\n      puts \"\"\n      puts \"El examen va a comenzar.\"\n      puts \"  Debe responder poniendo solo el caracter de la respuesta elegida.\"\n      print \" El examen consta de \"\n      print @numeroPreguntas\n      puts \" preguntas.\"\n    end\n    \n\n    def examinar(*resp)\n      cabecera\n      for i in 1..@numeroPreguntas\n        puts mostrarPregunta(i)\n        input(i,resp[i-1])\n      end\n      return notaFinal\n    end\n    \n    def invertir\n      @contenido = invertirExamen(@contenido)\n    end    \n    \n    \n  end\nend","undoManager":{"mark":98,"position":100,"stack":[[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":3,"column":2},"end":{"row":3,"column":3}},"text":"t"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":3,"column":1},"end":{"row":3,"column":2}},"text":"a"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":3,"column":0},"end":{"row":3,"column":1}},"text":"#"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":2,"column":19},"end":{"row":3,"column":0}},"text":"\n"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":2,"column":0},"end":{"row":2,"column":4}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"removeLines","range":{"start":{"row":1,"column":0},"end":{"row":2,"column":0}},"nl":"\n","lines":[""]}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":1,"column":0},"end":{"row":2,"column":0}},"text":"\n"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":3,"column":0},"end":{"row":4,"column":0}},"text":"\n"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":5,"column":0},"end":{"row":5,"column":4}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":5,"column":4},"end":{"row":5,"column":8}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":6,"column":0},"end":{"row":6,"column":4}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":6,"column":4},"end":{"row":6,"column":8}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":7,"column":0},"end":{"row":7,"column":4}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":7,"column":4},"end":{"row":7,"column":8}},"text":"    "}]}],[{"group":"doc","deltas":[{"action":"insertText","range":