e","","    def initialize(exam)","        @theExam = exam","        @userAnswers = Array.new(@theExam.numQuestion)","        @numRight = 0","    end","","    def make_exam","        i = 0","        puts @theExam.printQuestion","        while @userAnswers.size() > i","        puts @theExam.printQuestion","        print \"--> Respuesta = \"","        @userAnswers[i] = gets.chomp","        if @userAnswers[i] == @theExam.examAnswers[i]","        @numRight += 1","        end","        i += 1","        end","        puts \" \\n---- Resultado del examen ------ \"","        if pass","        puts \" ## --> Aprobado.\"","        else","        puts \" ## --> Suspendido.\"","        end","    end","    ","    private","    def pass","        @userAnswers.size()/2 < @numRight","    end"]},{"action":"insertText","range":{"start":{"row":0,"column":0},"end":{"row":0,"column":14}},"text":"class Interfaz"},{"action":"insertText","range":{"start":{"row":0,"column":14},"end":{"row":1,"column":0}},"text":"\n"},{"action":"insertLines","range":{"start":{"row":1,"column":0},"end":{"row":10,"column":0}},"lines":["   attr_accessor :examen ","    ","    def initialize(ex)","       @examen = ex","    end","    ","    def mostrar_pregunta","       puts @examen.exam.get_ini().pregunta","    end"]},{"action":"insertText","range":{"start":{"row":10,"column":0},"end":{"row":10,"column":3}},"text":"end"}]}],[{"group":"doc","deltas":[{"start":{"row":0,"column":0},"end":{"row":10,"column":3},"action":"remove","lines":["class Interfaz","   attr_accessor :examen ","    ","    def initialize(ex)","       @examen = ex","    end","    ","    def mostrar_pregunta","       puts @examen.exam.get_ini().pregunta","    end","end"]},{"start":{"row":0,"column":0},"end":{"row":90,"column":3},"action":"insert","lines":["# -*- coding: utf-8 -*-","require 'rubygems'","require 'bundler/setup'","require 'rspec'","","module Exam","  class UI","    attr_reader :contenido, :aciertos, :nota","    ","    ","    def initialize (examen)","      @contenido = examen","      @aciertos = 0","      @nota = -1","      @numeroPreguntas = examen.lista_preguntas.count","    end","    ","    ","    def mostrarPregunta(numero)","      print \"Pregunta\"","      puts numero","      return @contenido.obtenerPregunta(numero)","    end","    ","    ","    def input(numeroPregunta, resp=0)","      puts \"\"","      print \"Respuesta: \"","      STDOUT.flush","      if resp == 0 then","        respuesta = gets.chomp","      else","        respuesta = resp","        puts respuesta","      end","      puts \"\"","      puts \"\"","      puts \"\"","      puts \"\"","      comparar(numeroPregunta, respuesta)","      return true","    end","    ","    ","    def comparar(numeroPregunta, respuesta)","      if respuesta == @contenido.lista_soluciones.obtenerValor(numeroPregunta) then","        @aciertos = @aciertos + 1","        return true","      end","    end","","","    def notaFinal","      print \"Aciertos: \"","      print @aciertos","      print \" / \"","      puts @numeroPreguntas","      print \"Su nota ha sido: \"","      @nota = ((@aciertos * 10) / @numeroPreguntas)","      print @nota","      return @nota","    end","    ","    ","    def cabecera","      puts \"\"","      puts \"\"","      puts \"El examen va a comenzar.\"","      puts \"  Debe responder poniendo solo el caracter de la respuesta elegida.\"","      print \" El examen consta de \"","      print @numeroPreguntas","      puts \" preguntas.\"","    end","    ","","    def examinar(*resp)","      cabecera","      for i in 1..@numeroPreguntas","        puts mostrarPregunta(i)","        input(i,resp[i-1])","      end","      return notaFinal","    end","    ","    def invertir","      @contenido = invertirExamen(@contenido)","    end    ","    ","    ","  end","end"]}]}],[{"group":"doc","deltas":[{"start":{"row":5,"column":11},"end":{"row":5,"column":15},"action":"insert","lines":["enes"]}]}],[{"group":"doc","deltas":[{"start":{"row":